mapper.select:
select i.`id`, i.collector_id as collectorId, i.item_type as itemType, i.`code`, i.`name`, i.`description`, i.data_type as dataType, i.data_unit as dataUnit, i.coefficient, i.max_value as `maxValue`, i.`state` from a_item_group g LEFT JOIN a_item_group_mapper m on m.group_id = g.id LEFT JOIN a_item i on i.id = m.item_id where g.id in
#{item}
order by i.item_type asc, i.collector_id asc, i.name asc

SELECT g.id, g.code, g.type, g.name, g.parent, g.area, g.note, g.building_id as buildingId FROM a_item_group g where building_id = #{buildingId} and `type` = #{subType} and left(code, 2) = #{type}
AND parent = #{parent}
-- 拿到所有记录 -- AND (parent is null or parent = "" or parent <= 0)
order by code asc
